package github.com.miguelfreelancer56577.hackerrank;

import java.util.stream.Stream;

class Solution2 {
	public static void main(String[] args) {
		Solution2 solution = new Solution2();
		System.out.println("Operations: " + solution.solution(""));
	}
    
	public int solution(String binaryRepresentation) {
        // write your code in Java SE 8
		final int decimalRepresentation = fromBinaryToDecimal(binaryRepresentation);
		return getNumberOfOperations(decimalRepresentation);
    }
	
	private int getNumberOfOperations(int number) {
		//it initializes counter 
		int count = 0;
		
		//it returns 0 for no operations
		if(number == 0) {
			return 0;
		}
		
		//it counts the numbers of operations done on the number 
		while(number != 0) {
			if ( number % 2 == 0 ) {
				number = number/2;
			}
			else {
			number -= 1;
			}
			count++;
		}
		return count;
	}
	
	private int fromBinaryToDecimal(String binaryRepresentation) {
		try {
			return Integer.parseInt(binaryRepresentation, 2);
		} catch (NumberFormatException e) {
			return 0;
		}
	}

}
